package main

import "fmt"

func main() {
	firstDistinct := func(n int) int {
		var counts [128]int
		for i := range input {
			for j := 0; j < n; j++ {
				counts[input[i+j]]++
			}

			allOK := true
			for j := 0; j < n; j++ {
				allOK = allOK && counts[input[i+j]] == 1
			}
			if allOK {
				return i + n
			}

			for j := 0; j < n; j++ {
				counts[input[i+j]]--
			}
		}
		return -1
	}

	fmt.Println("part 1)", firstDistinct(4))
	fmt.Println("part 2)", firstDistinct(14))
}

const input = ``
